import React, { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [messages, setMessages] = useState([]);
  const [ws, setWs] = useState(null);

  useEffect(() => {
    // Connect to FastAPI WebSocket
    const socket = new WebSocket("ws://localhost:7001/ws");

    socket.onopen = () => {
      console.log("âœ… Connected to CV Service");
    };

    socket.onmessage = (event) => {
      const data = event.data;
      console.log("ðŸ“© Message received:", data);

      // Append message to list
      setMessages((prev) => [...prev, data]);
    };

    socket.onclose = () => {
      console.log("âŒ Disconnected from CV Service");
    };

    setWs(socket);

    // cleanup
    return () => {
      socket.close();
    };
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <h1>ðŸ“¡ AI/ML Real-Time Messaging System</h1>
        <p>
          Messages detected by Computer Vision service will appear here:
        </p>
        <ul>
          {messages.map((msg, index) => (
            <li key={index}>{msg}</li>
          ))}
        </ul>
      </header>
    </div>
  );
}

export default App;
