import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import {
  Box,
  Button,
  TextField,
  Typography,
  Avatar,
  Paper,
  AppBar,
  Toolbar,
  Container,
  List,
  ListItem,
  ListItemAvatar,
  ListItemText,
  Divider,
  CircularProgress,
  Stack
} from '@mui/material';
import PhotoCamera from '@mui/icons-material/PhotoCamera';

const socket = io('http://localhost:5000');

function App() {
  const [username, setUsername] = useState('');
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [objectLabel, setObjectLabel] = useState('');
  const [imageData, setImageData] = useState('');
  const [cvLoading, setCvLoading] = useState(false);
  const [webcamStarted, setWebcamStarted] = useState(false);
  const videoRef = useRef(null);

  useEffect(() => {
    socket.on('message', msg => setMessages(prev => [...prev, msg]));
    return () => socket.off('message');
  }, []);

  const handleStartWebcam = () => {
    navigator.mediaDevices.getUserMedia({ video: true })
      .then(stream => {
        videoRef.current.srcObject = stream;
        setWebcamStarted(true);
      })
      .catch(err => {
        alert('Camera error: ' + err.message);
      });
  };

  const captureAndDetect = async () => {
    setCvLoading(true);
    setObjectLabel('');
    setImageData('');
    const canvas = document.createElement('canvas');
    canvas.width = videoRef.current.videoWidth;
    canvas.height = videoRef.current.videoHeight;
    canvas.getContext('2d').drawImage(videoRef.current, 0, 0);

    canvas.toBlob(async (blob) => {
      try {
        const formData = new FormData();
        formData.append('image', blob, 'snapshot.jpg');
        const resp = await fetch('http://localhost:8000/detect', {
          method: 'POST',
          body: formData,
        });
        if (!resp.ok) throw new Error('CV API error');
        const data = await resp.json();
        setObjectLabel(data.label || '');
        setImageData(canvas.toDataURL('image/jpeg'));
      } catch (e) {
        alert('Failed to fetch from backend: ' + e.message);
      } finally {
        setCvLoading(false);
      }
    }, 'image/jpeg');
  };

  const handleSend = () => {
    if (!username || !input || !objectLabel || !imageData) {
      alert('Please enter username, message, and detect an object before sending!');
      return;
    }
    const msg = {
      sender: username,
      text: input,
      object: objectLabel,
      image: imageData,
      time: new Date().toLocaleTimeString(),
    };
    socket.emit('message', msg);
    setMessages((prev) => [...prev, msg]);
    setInput('');
    setObjectLabel('');
    setImageData('');
  };

  return (
    <Box sx={{ bgcolor: "#f7fafc", minHeight: "100vh" }}>
      <AppBar position="static" sx={{ background: "#283593" }}>
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Disaster-Ready AI Messaging
          </Typography>
        </Toolbar>
      </AppBar>
      <Container maxWidth="md" sx={{ mt: 4 }}>
        <Paper elevation={3} sx={{ p: 4, borderRadius: 3, background: "#fff" }}>
          <Stack spacing={2} alignItems="center">
            <Typography variant="h5" color="primary" sx={{ fontWeight: 'bold' }}>
              Welcome to Vision+Chat
            </Typography>
            <TextField
              label="Username"
              value={username}
              onChange={e => setUsername(e.target.value)}
              fullWidth
              variant="outlined"
              sx={{ maxWidth: 320 }}
            />
            <Stack direction={{ xs: 'column', sm: 'row' }} spacing={2} alignItems="flex-start" width="100%">
              <Box>
                <video
                  ref={videoRef}
                  width={260}
                  height={200}
                  autoPlay
                  style={{
                    display: webcamStarted ? "block" : "none",
                    borderRadius: 8,
                    border: "1px solid #ccc",
                    marginBottom: 6,
                  }}
                />
                {!webcamStarted && (
                  <Button
                    startIcon={<PhotoCamera />}
                    variant="contained"
                    onClick={handleStartWebcam}
                    sx={{ width: 260, mb: 1 }}
                  >
                    Start Webcam
                  </Button>
                )}
                {webcamStarted &&
                  <Button
                    variant="outlined"
                    onClick={captureAndDetect}
                    fullWidth
                    color="secondary"
                    sx={{ mb: 1 }}
                    disabled={cvLoading}
                  >
                    {cvLoading ? <CircularProgress size={18} color="secondary" /> : "Detect Object"}
                  </Button>
                }
                {objectLabel && (
                  <Typography color="secondary.main" sx={{ mt: 1, fontWeight: 500 }}>
                    Detected: <b>{objectLabel}</b>
                  </Typography>
                )}
                {imageData && (
                  <Avatar
                    src={imageData}
                    alt="Detected"
                    sx={{ width: 60, height: 60, mt: 1, border: '2px solid #283593' }}
                  />
                )}
              </Box>
              <Box flex={1}>
                <Paper elevation={0} sx={{ maxHeight: 240, overflowY: "auto", bgcolor: "#f4f6fa", mb: 2 }}>
                  <List disablePadding>
                    {messages.map((msg, i) => (
                      <React.Fragment key={i}>
                        <ListItem alignItems={msg.sender === username ? "right" : "left"}>
                          <ListItemAvatar>
                            <Avatar sx={{ bgcolor: "#283593" }}>
                              {msg.sender?.slice(0, 2).toUpperCase()}
                            </Avatar>
                          </ListItemAvatar>
                          <ListItemText
                            primary={
                              <Box sx={{
                                bgcolor: msg.sender === username ? "#c5cae9" : "#e3f2fd",
                                display: "inline-block",
                                borderRadius: 3,
                                px: 2, py: 1,
                                fontWeight: 500,
                                fontSize: 15
                              }}>
                                {msg.text}
                              </Box>
                            }
                            secondary={
                              <Box component="span" sx={{ fontSize: 13 }}>
                                {msg.sender} &ensp;|&ensp; {msg.object}
                                <br />
                                <span style={{ fontSize: 11, color: "#888" }}>{msg.time}</span>
                                {msg.image &&
                                  <Avatar src={msg.image} variant="rounded" alt="object"
                                    sx={{ width: 36, height: 36, mt: .5, border: '1px solid #bbb' }} />}
                              </Box>
                            }
                          />
                        </ListItem>
                        {i < messages.length - 1 && <Divider variant="inset" />}
                      </React.Fragment>
                    ))}
                  </List>
                </Paper>
                <Stack direction="row" spacing={2}>
                  <TextField
                    label="Type your message..."
                    value={input}
                    onChange={e => setInput(e.target.value)}
                    variant="outlined"
                    fullWidth
                    onKeyDown={e => { if (e.key === 'Enter') handleSend(); }}
                  />
                  <Button
                    onClick={handleSend}
                    variant="contained"
                    size="large"
                    disabled={!username || !input || !objectLabel}
                    sx={{ minWidth: 120 }}
                  >
                    Send
                  </Button>
                </Stack>
              </Box>
            </Stack>
          </Stack>
        </Paper>
      </Container>
    </Box>
  );
}

export default App;
