import React, { useState, useEffect, useRef } from "react";

export default function App() {
  const videoRef = useRef(null);
  const wsRef = useRef(null);

  const [image, setImage] = useState(null);
  const [detections, setDetections] = useState([]);
  const [selectedObject, setSelectedObject] = useState(null);
  const [messages, setMessages] = useState({});
  const [inputMsg, setInputMsg] = useState("");

  // ------------------- WebSocket -------------------
  useEffect(() => {
    const ws = new WebSocket("ws://localhost:7001/ws");
    ws.onopen = () => console.log("WebSocket connected!");
    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);

      if (data.type === "detection_broadcast") {
        setImage(`data:image/jpeg;base64,${data.image}`);
        setDetections(data.detections);
      } else if (data.type === "new_message") {
        const { object_id, message } = data;
        setMessages((prev) => ({
          ...prev,
          [object_id]: [...(prev[object_id] || []), message],
        }));
      }
    };
    wsRef.current = ws;
    return () => ws.close();
  }, []);

  // ------------------- Camera -------------------
  useEffect(() => {
    navigator.mediaDevices
      .getUserMedia({ video: true })
      .then((stream) => {
        videoRef.current.srcObject = stream;
        videoRef.current.play();
      });
  }, []);

  // ------------------- Send frames -------------------
  useEffect(() => {
    const interval = setInterval(() => {
      if (!videoRef.current) return;
      const canvas = document.createElement("canvas");
      canvas.width = 640;
      canvas.height = 480;
      const ctx = canvas.getContext("2d");
      ctx.drawImage(videoRef.current, 0, 0, 640, 480);
      canvas.toBlob(async (blob) => {
        const formData = new FormData();
        formData.append("file", blob, "frame.jpg");
        await fetch("http://localhost:7001/detect", {
          method: "POST",
          body: formData,
        });
      }, "image/jpeg");
    }, 200); // 5 FPS
    return () => clearInterval(interval);
  }, []);

  // ------------------- Send message -------------------
  const sendMessage = () => {
    if (!selectedObject || !inputMsg) return;
    wsRef.current.send(
      JSON.stringify({
        type: "send_message",
        object_id: selectedObject,
        user_id: "user_1",
        text: inputMsg,
      })
    );
    setInputMsg("");
  };

  // ------------------- AES-GCM Decrypt -------------------
  const base64ToArrayBuffer = (base64) => {
    const binary = window.atob(base64);
    const len = binary.length;
    const bytes = new Uint8Array(len);
    for (let i = 0; i < len; i++) bytes[i] = binary.charCodeAt(i);
    return bytes.buffer;
  };

  const decryptAESGCM = async (ciphertextB64, ivB64, keyB64) => {
    try {
      const keyBuffer = base64ToArrayBuffer(keyB64);
      const cryptoKey = await window.crypto.subtle.importKey(
        "raw",
        keyBuffer,
        { name: "AES-GCM" },
        false,
        ["decrypt"]
      );
      const iv = base64ToArrayBuffer(ivB64);
      const ct = base64ToArrayBuffer(ciphertextB64);
      const decrypted = await window.crypto.subtle.decrypt(
        { name: "AES-GCM", iv },
        cryptoKey,
        ct
      );
      return new TextDecoder().decode(decrypted);
    } catch {
      return "[Decryption Failed]";
    }
  };

  // ------------------- Display -------------------
  return (
    <div style={{ padding: "20px" }}>
      <h1>ðŸ“¡ AI/ML Real-Time Messaging System</h1>

      {/* Hidden Camera */}
      <video ref={videoRef} style={{ display: "none" }} />

      {/* Detection Image */}
      <div style={{ position: "relative", display: "inline-block" }}>
        {image && <img src={image} alt="Detection" style={{ maxWidth: "800px" }} />}
        {detections.map((det) => {
          const [x1, y1, x2, y2] = det.bbox;
          const width = x2 - x1;
          const height = y2 - y1;
          return (
            <div
              key={det.object_id}
              onClick={() => setSelectedObject(det.object_id)}
              style={{
                position: "absolute",
                left: x1,
                top: y1,
                width,
                height,
                border: "2px solid red",
                cursor: "pointer",
                backgroundColor:
                  selectedObject === det.object_id ? "rgba(255,0,0,0.2)" : "transparent",
              }}
            >
              {det.class}
            </div>
          );
        })}
      </div>

      {/* Message Box */}
      <div style={{ marginTop: "20px" }}>
        <h3>Selected Object: {selectedObject || "None"}</h3>
        <div
          style={{
            maxHeight: "200px",
            overflowY: "auto",
            border: "1px solid gray",
            padding: "10px",
          }}
        >
          {selectedObject && messages[selectedObject]
            ? messages[selectedObject].map((msg) => {
                const [plaintext, setPlaintext] = useState("[Decrypting...]");
                useEffect(() => {
                  if (msg.text_enc) {
                    decryptAESGCM(msg.text_enc.ciphertext, msg.text_enc.iv, msg.text_enc.key).then(
                      (text) => setPlaintext(text)
                    );
                  }
                }, [msg]);
                return (
                  <div key={msg.id}>
                    <b>{msg.user_id}:</b> {plaintext}
                  </div>
                );
              })
            : "No messages yet."}
        </div>
        <input
          type="text"
          value={inputMsg}
          onChange={(e) => setInputMsg(e.target.value)}
          placeholder="Type message..."
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
}
