import React, { useRef, useEffect, useState } from "react";

function App() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [detections, setDetections] = useState([]);

  useEffect(() => {
    // 🎥 Access webcam
    navigator.mediaDevices.getUserMedia({ video: true }).then((stream) => {
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
      }
    });

    // ⏱ Send a frame every 500ms (2 FPS)
    const interval = setInterval(() => {
      captureAndSend();
    }, 500);

    return () => clearInterval(interval);
  }, []);

  const captureAndSend = async () => {
    if (!videoRef.current || !canvasRef.current) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    // Draw a smaller image (resize to 320x240)
    ctx.drawImage(videoRef.current, 0, 0, 320, 240);

    canvas.toBlob(async (blob) => {
      if (!blob) return;

      const formData = new FormData();
      formData.append("file", blob, "frame.jpg");

      try {
        // Send frame to backend CV service
        const response = await fetch("http://localhost:7001/detect", {
          method: "POST",
          body: formData,
        });

        const data = await response.json();
        setDetections(data.detections || []);
      } catch (err) {
        console.error("Error sending frame:", err);
      }
    }, "image/jpeg");
  };

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold">📡 Real-Time Detection</h1>
      <video ref={videoRef} autoPlay playsInline width="320" height="240" />
      <canvas ref={canvasRef} width="320" height="240" style={{ display: "none" }} />

      <div className="mt-4">
        <h2 className="text-lg">Detections:</h2>
        <ul>
          {detections.map((det, i) => (
            <li key={i}>
              {det.class} ({Math.round(det.conf * 100)}%)
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
