// App.js
import React, { useEffect, useRef, useState } from "react";

// small helpers for base64 <-> ArrayBuffer
function base64ToArrayBuffer(base64) {
  const binary = window.atob(base64);
  const len = binary.length;
  const bytes = new Uint8Array(len);
  for (let i = 0; i < len; i++) bytes[i] = binary.charCodeAt(i);
  return bytes.buffer;
}
function arrayBufferToBase64(buffer) {
  const bytes = new Uint8Array(buffer);
  let binary = "";
  for (let i = 0; i < bytes.byteLength; i++) binary += String.fromCharCode(bytes[i]);
  return window.btoa(binary);
}

export default function App() {
  const videoRef = useRef(null);
  const wsRef = useRef(null);

  const [image, setImage] = useState(null);
  const [detections, setDetections] = useState([]);
  const [selectedObject, setSelectedObject] = useState(null);
  const [messages, setMessages] = useState({}); // object_id -> [message]
  const [objectKeys, setObjectKeys] = useState({}); // object_id -> base64 key
  const [inputMsg, setInputMsg] = useState("");
  const userId = useRef("user_" + Math.floor(Math.random() * 10000)).current;

  // WebSocket connect
  useEffect(() => {
    const ws = new WebSocket("ws://localhost:7001/ws");
    ws.onopen = () => console.log("WS connected");
    ws.onmessage = (evt) => {
      let data = null;
      try {
        data = JSON.parse(evt.data);
      } catch (e) {
        console.warn("Non-JSON WS msg ignored", evt.data);
        return;
      }

      if (data.type === "info") {
        console.log("INFO:", data.message);
      } else if (data.type === "detection_broadcast") {
        setImage(`data:image/jpeg;base64,${data.image}`);
        setDetections(data.detections || []);
        // update keys mapping (if provided)
        if (data.keys) {
          setObjectKeys((prev) => ({ ...prev, ...data.keys }));
        }
      } else if (data.type === "new_message") {
        const { object_id, message } = data;
        setMessages((prev) => {
          const arr = prev[object_id] ? [...prev[object_id]] : [];
          arr.push(message);
          return { ...prev, [object_id]: arr };
        });
      }
    };
    wsRef.current = ws;
    return () => ws.close();
  }, []);

  // Camera + frame sending (320x240 to reduce payload), 5 FPS
  useEffect(() => {
    let interval = null;
    const start = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        if (!videoRef.current) return;
        videoRef.current.srcObject = stream;

        // Wait for metadata loaded then play if needed
        await new Promise((resolve) => {
          videoRef.current.onloadedmetadata = resolve;
        });
        try {
          if (videoRef.current.paused) await videoRef.current.play();
        } catch (err) {
          console.warn("Video play interrupted:", err);
        }

        interval = setInterval(() => {
          if (!videoRef.current) return;
          const canvas = document.createElement("canvas");
          canvas.width = 320; // smaller frame
          canvas.height = 240;
          const ctx = canvas.getContext("2d");
          ctx.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);
          canvas.toBlob(
            async (blob) => {
              if (!blob) return;
              const fd = new FormData();
              fd.append("file", blob, "frame.jpg");
              try {
                await fetch("http://localhost:7001/detect", {
                  method: "POST",
                  body: fd,
                });
              } catch (err) {
                console.warn("Frame send failed:", err);
              }
            },
            "image/jpeg",
            0.7
          );
        }, 200); // 5 FPS
      } catch (err) {
        console.error("Camera error:", err);
      }
    };
    start();

    return () => {
      if (interval) clearInterval(interval);
      if (videoRef.current && videoRef.current.srcObject) {
        const tracks = videoRef.current.srcObject.getTracks();
        tracks.forEach((t) => t.stop());
      }
    };
  }, []);

  // encrypt using subtle with provided base64 key
  async function encryptForObject(object_id, plaintext) {
    const key_b64 = objectKeys[object_id];
    if (!key_b64) throw new Error("No key for object");
    const rawKey = base64ToArrayBuffer(key_b64);
    const cryptoKey = await window.crypto.subtle.importKey("raw", rawKey, { name: "AES-GCM" }, false, [
      "encrypt",
    ]);
    const iv = window.crypto.getRandomValues(new Uint8Array(12));
    const enc = new TextEncoder().encode(plaintext);
    const ct = await window.crypto.subtle.encrypt({ name: "AES-GCM", iv }, cryptoKey, enc);
    return {
      ciphertext: arrayBufferToBase64(ct),
      iv: arrayBufferToBase64(iv.buffer),
    };
  }

  // decrypt helper (for local preview; other clients will also decrypt)
  async function decryptForObject(object_id, text_enc) {
    try {
      const key_b64 = objectKeys[object_id];
      if (!key_b64) return "[no key]";
      const rawKey = base64ToArrayBuffer(key_b64);
      const cryptoKey = await window.crypto.subtle.importKey("raw", rawKey, { name: "AES-GCM" }, false, [
        "decrypt",
      ]);
      const ivBuf = base64ToArrayBuffer(text_enc.iv);
      const ctBuf = base64ToArrayBuffer(text_enc.ciphertext);
      const plainBuf = await window.crypto.subtle.decrypt({ name: "AES-GCM", iv: ivBuf }, cryptoKey, ctBuf);
      return new TextDecoder().decode(plainBuf);
    } catch (err) {
      console.warn("Decrypt failed", err);
      return "[decryption failed]";
    }
  }

  // sending encrypted message
  const sendMessage = async () => {
    if (!selectedObject || !inputMsg) return;
    try {
      const text_enc = await encryptForObject(selectedObject, inputMsg);
      const payload = {
        type: "send_message",
        object_id: selectedObject,
        user_id: userId,
        text_enc,
      };
      wsRef.current.send(JSON.stringify(payload));

      // locally append message (so sender sees it immediately, encrypted form)
      setMessages((prev) => {
        const arr = prev[selectedObject] ? [...prev[selectedObject]] : [];
        arr.push({ id: "tmp-" + Date.now(), user_id: userId, text_enc });
        return { ...prev, [selectedObject]: arr };
      });

      // optionally decrypt locally for display (instant)
      const plain = await decryptForObject(selectedObject, text_enc);
      setMessages((prev) => {
        const arr = prev[selectedObject].map((m) => (m.id && m.id.startsWith("tmp-") ? { ...m, plaintext: plain } : m));
        return { ...prev, [selectedObject]: arr };
      });
    } catch (err) {
      console.error("Send failed:", err);
    } finally {
      setInputMsg("");
    }
  };

  // decrypt visible messages for UI when messages or keys update
  useEffect(() => {
    const decryptAllVisible = async () => {
      if (!selectedObject) return;
      const msgs = messages[selectedObject] || [];
      const results = await Promise.all(
        msgs.map(async (m) => {
          if (m.plaintext) return m;
          if (m.text_enc) {
            const p = await decryptForObject(selectedObject, m.text_enc);
            return { ...m, plaintext: p };
          }
          return { ...m, plaintext: m.text || "" };
        })
      );
      setMessages((prev) => ({ ...prev, [selectedObject]: results }));
    };
    decryptAllVisible();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [selectedObject, objectKeys]); // re-run when selectedObject or keys change

  return (
    <div style={{ padding: 16 }}>
      <h2>📡 Real-Time Object Messaging (demo)</h2>

      {/* hidden camera */}
      <video ref={videoRef} style={{ display: "none" }} />

      <div style={{ position: "relative", display: "inline-block" }}>
        {image ? (
          <img src={image} alt="detection" style={{ width: 640, height: 480 }} />
        ) : (
          <div style={{ width: 640, height: 480, background: "#eee", display: "flex", alignItems: "center", justifyContent: "center" }}>
            Waiting for detections...
          </div>
        )}

        {/* overlay boxes */}
        {detections.map((d) => {
          const [x1, y1, x2, y2] = d.bbox;
          const width = x2 - x1;
          const height = y2 - y1;
          return (
            <div
              key={d.object_id}
              onClick={() => setSelectedObject(d.object_id)}
              style={{
                position: "absolute",
                left: x1,
                top: y1,
                width,
                height,
                border: selectedObject === d.object_id ? "3px solid #00ff99" : "2px solid red",
                background: selectedObject === d.object_id ? "rgba(0,255,153,0.12)" : "transparent",
                color: "#fff",
                fontWeight: "600",
                display: "flex",
                alignItems: "flex-start",
                justifyContent: "flex-start",
                pointerEvents: "auto",
              }}
            >
              <div style={{ background: "red", padding: "2px 6px", fontSize: 12 }}>{d.class}</div>
            </div>
          );
        })}
      </div>

      <div style={{ marginTop: 12 }}>
        <strong>Selected object:</strong> {selectedObject || "None"}
      </div>

      <div style={{ marginTop: 12, display: "flex", gap: 12 }}>
        <div style={{ width: 400 }}>
          <div style={{ height: 180, overflowY: "auto", border: "1px solid #ccc", padding: 8 }}>
            {selectedObject && (messages[selectedObject] || []).length > 0 ? (
              (messages[selectedObject] || []).map((m, i) => (
                <div key={m.id || i} style={{ marginBottom: 6 }}>
                  <strong>{m.user_id}:</strong> {m.plaintext || "[encrypted]"}
                </div>
              ))
            ) : (
              <div>No messages for this object yet</div>
            )}
          </div>

          <div style={{ marginTop: 8, display: "flex", gap: 8 }}>
            <input
              value={inputMsg}
              onChange={(e) => setInputMsg(e.target.value)}
              placeholder={selectedObject ? "Type message..." : "Select an object first"}
              style={{ flex: 1 }}
            />
            <button onClick={sendMessage} disabled={!selectedObject || !inputMsg}>
              Send
            </button>
          </div>
        </div>

        <div style={{ width: 220 }}>
          <div style={{ fontWeight: "600", marginBottom: 6 }}>Object keys (demo)</div>
          <div style={{ background: "#fafafa", padding: 8, border: "1px solid #eee", height: 180, overflowY: "auto" }}>
            {Object.keys(objectKeys).length === 0 && <div>No keys yet</div>}
            {Object.entries(objectKeys).map(([id, k]) => (
              <div key={id} style={{ marginBottom: 6 }}>
                <div style={{ fontSize: 12 }}>{id}</div>
                <div style={{ fontSize: 10, color: "#666", wordBreak: "break-all" }}>{k}</div>
              </div>
            ))}
          </div>
          <div style={{ marginTop: 8, fontSize: 12, color: "#666" }}>
            Keys are included in detection broadcasts for demo only. In production, use secure key exchange.
          </div>
        </div>
      </div>
    </div>
  );
}
