import React, { useEffect, useRef, useState } from "react";

// Simple XOR-based encryption for demo
function encryptMessage(message, key) {
  let encrypted = "";
  for (let i = 0; i < message.length; i++) {
    encrypted += String.fromCharCode(
      message.charCodeAt(i) ^ key.charCodeAt(i % key.length)
    );
  }
  return btoa(encrypted);
}

function decryptMessage(ciphertext, key) {
  let decoded = atob(ciphertext);
  let decrypted = "";
  for (let i = 0; i < decoded.length; i++) {
    decrypted += String.fromCharCode(
      decoded.charCodeAt(i) ^ key.charCodeAt(i % key.length)
    );
  }
  return decrypted;
}

function App() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [detections, setDetections] = useState([]);
  const [messages, setMessages] = useState([]);
  const [objectKeys, setObjectKeys] = useState({});
  const [selectedLabel, setSelectedLabel] = useState("");
  const [chatInput, setChatInput] = useState("");
  const wsRef = useRef(null);

  useEffect(() => {
    // Start camera
    navigator.mediaDevices.getUserMedia({ video: true }).then((stream) => {
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
      }
    });

    // WebSocket connection
    wsRef.current = new WebSocket("ws://localhost:8000/ws");

    wsRef.current.onmessage = (event) => {
      const data = JSON.parse(event.data);

      if (data.type === "detection_broadcast") {
        setDetections(data.detections);
        setObjectKeys(data.keys);
      }

      if (data.type === "chat") {
        const { label, ciphertext } = data;
        const key = objectKeys[label];
        if (key) {
          const msg = decryptMessage(ciphertext, key);
          setMessages((prev) => [...prev, { label, msg }]);
        }
      }
    };

    return () => wsRef.current && wsRef.current.close();
  }, [objectKeys]);

  useEffect(() => {
    // Draw bounding boxes and chat bubbles
    const ctx = canvasRef.current?.getContext("2d");
    if (ctx && videoRef.current) {
      const draw = () => {
        ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);

        detections.forEach((det) => {
          const [x1, y1, x2, y2] = det.bbox;

          // Draw box
          ctx.strokeStyle = "lime";
          ctx.lineWidth = 2;
          ctx.strokeRect(x1, y1, x2 - x1, y2 - y1);

          ctx.fillStyle = "lime";
          ctx.font = "16px Arial";
          ctx.fillText(`${det.label}`, x1, y1 > 20 ? y1 - 5 : y1 + 15);

          // Show messages for this label
          const labelMsgs = messages.filter((m) => m.label === det.label);
          labelMsgs.slice(-2).forEach((m, i) => {
            ctx.fillStyle = "yellow";
            ctx.font = "14px Arial";
            ctx.fillText(m.msg, x1, y2 + 20 + i * 18);
          });
        });

        requestAnimationFrame(draw);
      };
      draw();
    }
  }, [detections, messages]);

  // Periodic frame sending
  useEffect(() => {
    const interval = setInterval(() => {
      if (!videoRef.current) return;
      const canvas = document.createElement("canvas");
      canvas.width = 224;
      canvas.height = 224;
      const ctx = canvas.getContext("2d");
      ctx.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);

      canvas.toBlob((blob) => {
        if (blob) {
          const formData = new FormData();
          formData.append("file", blob, "frame.jpg");
          fetch("http://localhost:8000/detect/", {
            method: "POST",
            body: formData,
          }).catch((err) => console.error("Error sending frame:", err));
        }
      }, "image/jpeg");
    }, 1500); // send every 1.5s
    return () => clearInterval(interval);
  }, []);

  // Handle chat send
  const sendMessage = () => {
    if (!selectedLabel || !chatInput) return;
    const key = objectKeys[selectedLabel];
    if (!key) return;

    const ciphertext = encryptMessage(chatInput, key);

    wsRef.current.send(
      JSON.stringify({
        type: "chat",
        label: selectedLabel,
        ciphertext,
      })
    );

    setChatInput("");
  };

  return (
    <div className="p-6">
      <h1 className="text-xl font-bold mb-4">üîê Real-Time Encrypted Object Chat</h1>
      <div className="relative w-[640px] h-[480px] border rounded">
        <video ref={videoRef} autoPlay muted width="640" height="480" />
        <canvas
          ref={canvasRef}
          width="640"
          height="480"
          className="absolute top-0 left-0"
        />
      </div>

      <div className="mt-4 flex space-x-2">
        <select
          value={selectedLabel}
          onChange={(e) => setSelectedLabel(e.target.value)}
          className="border p-2"
        >
          <option value="">Select Object</option>
          {Object.keys(objectKeys).map((label) => (
            <option key={label} value={label}>
              {label}
            </option>
          ))}
        </select>
        <input
          type="text"
          value={chatInput}
          onChange={(e) => setChatInput(e.target.value)}
          placeholder="Enter message"
          className="border p-2 flex-grow"
        />
        <button onClick={sendMessage} className="bg-blue-500 text-white px-4 py-2 rounded">
          Send
        </button>
      </div>
    </div>
  );
}

export default App;
