import React, { useEffect, useState } from "react";

function App() {
  const [messages, setMessages] = useState([]);
  const [image, setImage] = useState(null);

  useEffect(() => {
    const ws = new WebSocket("ws://localhost:7001/ws");

    ws.onopen = () => {
      console.log("✅ Connected to WebSocket");
    };

    ws.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);

        // If server sends image + detections
        if (data.image) {
          setImage(`data:image/jpeg;base64,${data.image}`);
          setMessages(data.detections || []);
        } else {
          console.log("Message:", event.data);
        }
      } catch {
        console.log("Raw:", event.data);
      }
    };

    ws.onclose = () => {
      console.log("❌ Disconnected from WebSocket");
    };

    return () => ws.close();
  }, []);

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-4">
        📡 AI/ML Real-Time Messaging System
      </h1>

      {/* Show annotated detection image */}
      {image ? (
        <img
          src={image}
          alt="Detections"
          className="border rounded mb-4"
          width="640"
          height="480"
        />
      ) : (
        <p className="mb-4">Waiting for detection image...</p>
      )}

      {/* List detections */}
      <div>
        {messages.length > 0 ? (
          messages.map((det, i) => (
            <div key={i} className="p-2 mb-2 border rounded bg-gray-100">
              <strong>{det.class}</strong> (conf: {det.conf.toFixed(2)})
            </div>
          ))
        ) : (
          <p>No objects detected yet</p>
        )}
      </div>
    </div>
  );
}

export default App;
