// App.js
import React, { useState, useRef, useEffect } from "react";
import io from "socket.io-client";

// === Constants ===
const SIGNALING_URL = "http://localhost:5000";   // server.js
const CV_WS_URL = "ws://localhost:7001/ws";      // main.py detection broadcast
const CV_FRAME_URL = "ws://localhost:7001/ws_frames"; // main.py frame input

// detection decryption key (base64) printed by main.py at startup
const DETECTION_KEY_B64 = "<PASTE_KEY_FROM_MAINPY_HERE>";

// === AES-GCM helpers ===
async function importAesKey(b64) {
  const raw = Uint8Array.from(atob(b64), (c) => c.charCodeAt(0));
  return await window.crypto.subtle.importKey("raw", raw, "AES-GCM", false, [
    "encrypt",
    "decrypt",
  ]);
}

async function decryptAes(key, ct_b64, iv_b64) {
  try {
    const ct = Uint8Array.from(atob(ct_b64), (c) => c.charCodeAt(0));
    const iv = Uint8Array.from(atob(iv_b64), (c) => c.charCodeAt(0));
    const raw = await window.crypto.subtle.decrypt({ name: "AES-GCM", iv }, key, ct);
    return JSON.parse(new TextDecoder().decode(raw));
  } catch (e) {
    console.error("decrypt error", e);
    return null;
  }
}

export default function App() {
  const [username, setUsername] = useState("");
  const [connected, setConnected] = useState(false);
  const [socket, setSocket] = useState(null);

  const [detections, setDetections] = useState([]);
  const [selectedObject, setSelectedObject] = useState(null);

  const [chatLog, setChatLog] = useState([]);
  const [message, setMessage] = useState("");

  const videoRef = useRef();
  const canvasRef = useRef();
  const wsDetRef = useRef(null);
  const wsFrameRef = useRef(null);
  const detectionKeyRef = useRef(null);
  const socketRef = useRef(null);

  // === Setup camera and frame sender ===
  useEffect(() => {
    async function init() {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      if (videoRef.current) videoRef.current.srcObject = stream;

      // connect to detection broadcast WS
      wsDetRef.current = new WebSocket(CV_WS_URL);
      wsDetRef.current.onmessage = async (ev) => {
        const msg = JSON.parse(ev.data);
        if (msg.type === "detection_broadcast_enc" && msg.data) {
          const dec = await decryptAes(
            detectionKeyRef.current,
            msg.data.ciphertext_b64,
            msg.data.iv_b64
          );
          if (dec && dec.detections) setDetections(dec.detections);
        }
      };

      // connect to frames WS (send frames continuously)
      wsFrameRef.current = new WebSocket(CV_FRAME_URL);
      wsFrameRef.current.onopen = () => {
        console.log("Frame WS connected");
        const sendFrame = () => {
          if (
            videoRef.current &&
            wsFrameRef.current &&
            wsFrameRef.current.readyState === 1
          ) {
            const canvas = document.createElement("canvas");
            canvas.width = videoRef.current.videoWidth;
            canvas.height = videoRef.current.videoHeight;
            const ctx = canvas.getContext("2d");
            ctx.drawImage(videoRef.current, 0, 0);
            const dataUrl = canvas.toDataURL("image/jpeg", 0.5);
            wsFrameRef.current.send(dataUrl);
          }
          requestAnimationFrame(sendFrame);
        };
        requestAnimationFrame(sendFrame);
      };
    }
    importAesKey(DETECTION_KEY_B64).then((k) => (detectionKeyRef.current = k));
    init();
  }, []);

  // === Draw bounding boxes ===
  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    if (!videoRef.current) return;

    const draw = () => {
      if (!videoRef.current.videoWidth) {
        requestAnimationFrame(draw);
        return;
      }
      canvas.width = videoRef.current.videoWidth;
      canvas.height = videoRef.current.videoHeight;

      ctx.drawImage(videoRef.current, 0, 0);

      detections.forEach((det) => {
        const [x1, y1, x2, y2] = det.bbox;
        ctx.strokeStyle = det.object_id === selectedObject ? "lime" : "red";
        ctx.lineWidth = 2;
        ctx.strokeRect(x1, y1, x2 - x1, y2 - y1);

        ctx.fillStyle = "yellow";
        ctx.font = "14px Arial";
        ctx.fillText(det.class, x1 + 4, y1 + 14);

        // render chat messages above object
        chatLog
          .filter((m) => m.object_id === det.object_id)
          .slice(-3) // last 3 messages
          .forEach((m, idx) => {
            ctx.fillStyle = "rgba(0,0,0,0.6)";
            ctx.fillRect(x1, y1 - 40 - idx * 20, x2 - x1, 20);
            ctx.fillStyle = "white";
            ctx.fillText(`${m.sender}: ${m.text}`, x1 + 4, y1 - 26 - idx * 20);
          });
      });

      requestAnimationFrame(draw);
    };
    requestAnimationFrame(draw);
  }, [detections, selectedObject, chatLog]);

  // === Setup socket.io for chat ===
  const connectSocket = () => {
    const s = io(SIGNALING_URL);
    socketRef.current = s;
    setSocket(s);
    s.on("connect", () => {
      s.emit("register", { username });
      setConnected(true);
    });
    s.on("message", (msg) => {
      setChatLog((prev) => [...prev, msg]);
    });
  };

  const sendMessage = () => {
    if (!selectedObject) {
      alert("Select an object first!");
      return;
    }
    if (socketRef.current) {
      const msg = {
        sender: username,
        receiver: "all", // for demo, broadcast to all
        object_id: selectedObject,
        text: message,
        ts: Date.now(),
      };
      socketRef.current.emit("message", msg);
      setMessage("");
    }
  };

  // === Object click detection ===
  const handleCanvasClick = (e) => {
    const rect = canvasRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    for (const det of detections) {
      const [x1, y1, x2, y2] = det.bbox;
      if (x >= x1 && x <= x2 && y >= y1 && y <= y2) {
        setSelectedObject(det.object_id);
        return;
      }
    }
    setSelectedObject(null);
  };

  return (
    <div className="flex h-screen bg-gray-900 text-white">
      {/* Left: video canvas */}
      <div className="flex-1 flex items-center justify-center relative">
        <video ref={videoRef} autoPlay muted playsInline className="hidden" />
        <canvas ref={canvasRef} onClick={handleCanvasClick} className="w-full h-full" />
        {!connected && (
          <div className="absolute inset-0 flex flex-col items-center justify-center">
            <input
              className="p-2 text-black rounded"
              placeholder="Enter username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <button
              className="mt-2 px-4 py-2 bg-blue-600 rounded"
              onClick={connectSocket}
            >
              Join
            </button>
          </div>
        )}
      </div>

      {/* Right: chat sidebar */}
      <div className="absolute top-0 right-0 w-72 h-full bg-gray-900 border-l border-gray-700 flex flex-col">
        <div className="p-2 font-bold border-b border-gray-700">Chat</div>
        <div className="flex-1 overflow-y-auto p-2 space-y-2">
          {chatLog.map((m, idx) => {
            const isMe = m.sender === username;
            return (
              <div
                key={idx}
                className={`p-2 rounded ${
                  isMe ? "bg-blue-600 ml-auto text-right" : "bg-gray-700"
                }`}
              >
                <div className="text-xs opacity-75">
                  {isMe ? "Me" : m.sender} â†’ {m.receiver}
                </div>
                <div>{m.text}</div>
              </div>
            );
          })}
        </div>
        <div className="p-2 border-t border-gray-700 flex">
          <input
            className="flex-1 bg-gray-800 p-2 rounded text-sm outline-none"
            placeholder="Type message..."
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />
          <button
            className="ml-2 bg-green-600 px-3 py-1 rounded text-sm"
            onClick={sendMessage}
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
}
