import React, { useState, useRef, useEffect } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:5000'); // Realtime Node server

function App() {
  const [username, setUsername] = useState('');
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [objectLabel, setObjectLabel] = useState('');
  const [imageData, setImageData] = useState('');
  const [cvLoading, setCvLoading] = useState(false);

  const videoRef = useRef(null);

  useEffect(() => {
    socket.on('message', (msg) => {
      setMessages((prev) => [...prev, msg]);
    });
    return () => socket.off('message');
  }, []);

  // Webcam capture and send to CV server
  const captureAndDetect = async () => {
    setCvLoading(true);
    // Get a snapshot from webcam
    const canvas = document.createElement('canvas');
    canvas.width = videoRef.current.videoWidth;
    canvas.height = videoRef.current.videoHeight;
    canvas.getContext('2d').drawImage(videoRef.current, 0, 0);
    const blob = await new Promise((res) =>
      canvas.toBlob((b) => res(b), 'image/jpeg')
    );
    // Send to Python CV server
    const formData = new FormData();
    formData.append('image', blob, 'capture.jpg');

    const resp = await fetch('http://localhost:8000/detect', {
      method: 'POST',
      body: formData,
    });
    const data = await resp.json();
    setObjectLabel(data.label || '');
    setImageData(canvas.toDataURL('image/jpeg'));
    setCvLoading(false);
  };

  const handleSend = () => {
    if (!username || !input || !objectLabel || !imageData) {
      alert('Fill all fields and run object detection!');
      return;
    }
    const msg = {
      sender: username,
      text: input,
      object: objectLabel,
      image: imageData,
      time: new Date().toLocaleTimeString()
    };
    socket.emit('message', msg);
    setMessages((prev) => [...prev, msg]);
    setInput('');
  };

  return (
    <div style={{ padding: 32 }}>
      <h2>Disaster-Ready AI Messaging App</h2>
      <input
        placeholder="Enter Username"
        value={username}
        onChange={e => setUsername(e.target.value)}
        style={{ marginRight: 8 }}
      />
      <div>
        <video
          ref={videoRef}
          width={320}
          height={240}
          autoPlay
          style={{ marginTop: 16, border: '1px solid #ccc' }}
        />
        <button
          onClick={() => {
            navigator.mediaDevices.getUserMedia({ video: true }).then(stream => {
              videoRef.current.srcObject = stream;
            });
          }}
        >
          Start Webcam
        </button>
        <button onClick={captureAndDetect} disabled={cvLoading} style={{ marginLeft: 8 }}>
          {cvLoading ? 'Detecting...' : 'Detect Object'}
        </button>
        <div>
          {objectLabel && <p>Detected Object: <b>{objectLabel}</b></p>}
          {imageData && (
            <img src={imageData} alt="Captured" style={{ width: 80, height: 60, marginTop: 8 }} />
          )}
        </div>
      </div>
      <div style={{ marginTop: 16 }}>
        <input
          placeholder="Enter your message"
          value={input}
          onChange={e => setInput(e.target.value)}
          style={{ width: 300, marginRight: 8 }}
        />
        <button onClick={handleSend}>Send</button>
      </div>
      <div style={{ marginTop: 24, maxHeight: 200, overflowY: 'scroll', border: '1px solid #eee' }}>
        <h4>Messages:</h4>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {messages.map((msg, i) => (
            <li key={i} style={{ marginBottom: 12, borderBottom: '1px solid #fafafa' }}>
              <b>{msg.sender}:</b> {msg.text}
              <br />
              <small>
                Object: {msg.object} | {msg.time}
              </small>
              <br />
              {msg.image && (
                <img src={msg.image} alt="Object" style={{ width: 50, marginTop: 4 }} />
              )}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
