import React, { useEffect, useRef, useState } from "react";

function App() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [detections, setDetections] = useState([]);
  const [image, setImage] = useState(null);

  useEffect(() => {
    // Start webcam
    navigator.mediaDevices.getUserMedia({ video: true }).then((stream) => {
      videoRef.current.srcObject = stream;
    });

    // WebSocket for receiving annotated images
    const ws = new WebSocket("ws://localhost:7001/ws");
    ws.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);
        if (data.image) {
          setImage(`data:image/jpeg;base64,${data.image}`);
          setDetections(data.detections || []);
        }
      } catch {
        console.log("Raw:", event.data);
      }
    };

    // Interval: capture frame & send to /detect
    const interval = setInterval(() => {
      if (!videoRef.current || !canvasRef.current) return;

      const ctx = canvasRef.current.getContext("2d");
      ctx.drawImage(videoRef.current, 0, 0, 640, 480);

      canvasRef.current.toBlob(async (blob) => {
        if (!blob) return;
        const formData = new FormData();
        formData.append("file", blob, "frame.jpg");

        try {
          await fetch("http://localhost:7001/detect", {
            method: "POST",
            body: formData,
          });
        } catch (err) {
          console.error("Error sending frame:", err);
        }
      }, "image/jpeg", 0.7);
    }, 1000); // every 1 sec (adjust for speed vs lag)

    return () => {
      clearInterval(interval);
      ws.close();
    };
  }, []);

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-4">ðŸ“¡ Real-Time Object Detection</h1>

      {/* Webcam (hidden, used for capturing frames) */}
      <video
        ref={videoRef}
        autoPlay
        playsInline
        width="640"
        height="480"
        style={{ display: "none" }}
      />

      {/* Canvas (hidden, used for snapshot frames) */}
      <canvas
        ref={canvasRef}
        width="640"
        height="480"
        style={{ display: "none" }}
      />

      {/* Annotated detection image from server */}
      {image ? (
        <img
          src={image}
          alt="Detections"
          className="border rounded mb-4"
          width="640"
          height="480"
        />
      ) : (
        <p className="mb-4">Waiting for detection image...</p>
      )}

      {/* Detection results */}
      <div>
        {detections.length > 0 ? (
          detections.map((det, i) => (
            <div key={i} className="p-2 mb-2 border rounded bg-gray-100">
              <strong>{det.class}</strong> (conf: {det.conf.toFixed(2)})
            </div>
          ))
        ) : (
          <p>No objects detected</p>
        )}
      </div>
    </div>
  );
}

export default App;
