// App.js (React)
/* eslint-disable react-hooks/exhaustive-deps */
import React, { useEffect, useRef, useState } from "react";
import { io } from "socket.io-client";

// socket.io server
const SIO = io("http://localhost:5000");

// helpers: base64 <-> arraybuffer
function b64ToArrayBuffer(b64) {
  const binary = atob(b64);
  const len = binary.length;
  const bytes = new Uint8Array(len);
  for (let i = 0; i < len; i++) bytes[i] = binary.charCodeAt(i);
  return bytes.buffer;
}
function arrayBufferToB64(buf) {
  const bytes = new Uint8Array(buf);
  let bin = "";
  for (let i = 0; i < bytes.byteLength; i++) bin += String.fromCharCode(bytes[i]);
  return btoa(bin);
}

// ECDH (P-256) + AES-GCM helpers
async function generateECDHKeyPair() {
  const kp = await window.crypto.subtle.generateKey(
    { name: "ECDH", namedCurve: "P-256" },
    true,
    ["deriveKey"]
  );
  const pubRaw = await window.crypto.subtle.exportKey("raw", kp.publicKey); // raw format
  const pubB64 = arrayBufferToB64(pubRaw);
  return { keyPair: kp, publicKeyB64: pubB64 };
}
async function importPeerPublicKey(rawB64) {
  const raw = b64ToArrayBuffer(rawB64);
  return window.crypto.subtle.importKey("raw", raw, { name: "ECDH", namedCurve: "P-256" }, true, []);
}
async function deriveAESGCMKey(ownKP, peerRawB64) {
  const peerKey = await importPeerPublicKey(peerRawB64);
  const aesKey = await window.crypto.subtle.deriveKey(
    { name: "ECDH", public: peerKey },
    ownKP.privateKey,
    { name: "AES-GCM", length: 256 },
    false,
    ["encrypt", "decrypt"]
  );
  return aesKey;
}
async function exportRawKeyToB64(cryptoKey) {
  const raw = await window.crypto.subtle.exportKey("raw", cryptoKey);
  return arrayBufferToB64(raw);
}
async function encryptAesGcm(key, plaintext) {
  const iv = window.crypto.getRandomValues(new Uint8Array(12));
  const enc = new TextEncoder().encode(plaintext);
  const ct = await window.crypto.subtle.encrypt({ name: "AES-GCM", iv }, key, enc);
  return { ciphertext: arrayBufferToB64(ct), iv: arrayBufferToB64(iv.buffer) };
}
async function decryptAesGcm(key, ciphertextB64, ivB64) {
  const ivBuf = b64ToArrayBuffer(ivB64);
  const ctBuf = b64ToArrayBuffer(ciphertextB64);
  const plainBuf = await window.crypto.subtle.decrypt({ name: "AES-GCM", iv: new Uint8Array(ivBuf) }, key, ctBuf);
  return new TextDecoder().decode(plainBuf);
}

export default function App() {
  // video refs
  const videoRef = useRef(null);
  const overlayRef = useRef(null);

  // user / presence
  const [username, setUsername] = useState("");
  const [peer, setPeer] = useState("");
  const [connected, setConnected] = useState(false);

  // detection state
  const [detections, setDetections] = useState([]); // tracked objects
  const [selected, setSelected] = useState(null);   // selected object_id

  // E2EE keys: local ECDH keypair and derived AES keys per peer username
  const localKeyPairRef = useRef(null);
  const localPubB64Ref = useRef(null);
  const [peerPubMap, setPeerPubMap] = useState({});    // username -> pubB64
  const derivedKeysRef = useRef({});                   // username -> CryptoKey (AES-GCM)

  // messages per object
  const [chats, setChats] = useState({}); // object_id -> [{sender, plaintext, ts}]

  // chat input
  const [input, setInput] = useState("");

  // detection WS (from backend main.py) for detection_broadcasts
  const detectWSRef = useRef(null);

  // initialize local ECDH keypair on mount
  useEffect(() => {
    (async () => {
      const { keyPair, publicKeyB64 } = await generateECDHKeyPair();
      localKeyPairRef.current = keyPair;
      localPubB64Ref.current = publicKeyB64;
    })();
  }, []);

  // socket.io event handling
  useEffect(() => {
    SIO.on("connect", () => setConnected(true));
    SIO.on("disconnect", () => setConnected(false));

    // receive regular messages (encrypted) forwarded by server
    SIO.on("message", async (msg) => {
      // msg: { sender, receiver, object_id, text_enc: {ciphertext, iv}, ts }
      const { sender, object_id, text_enc, ts } = msg;
      // try decrypt if we have a derived key for sender
      const aesKey = derivedKeysRef.current[sender];
      if (aesKey && text_enc) {
        try {
          const plaintext = await decryptAesGcm(aesKey, text_enc.ciphertext, text_enc.iv);
          setChats(prev => {
            const arr = prev[object_id] ? [...prev[object_id]] : [];
            arr.push({ sender, plaintext, ts: ts || Date.now() });
            return { ...prev, [object_id]: arr };
          });
        } catch (e) {
          // decryption failed -> store placeholder
          setChats(prev => {
            const arr = prev[object_id] ? [...prev[object_id]] : [];
            arr.push({ sender, plaintext: "[decryption_failed]", ts: ts || Date.now() });
            return { ...prev, [object_id]: arr };
          });
        }
      } else {
        // no derived key yet -> store encrypted placeholder
        setChats(prev => {
          const arr = prev[object_id] ? [...prev[object_id]] : [];
          arr.push({ sender, plaintext: "[encrypted]", ts: ts || Date.now() });
          return { ...prev, [object_id]: arr };
        });
      }
    });

    // server responds to a get_pubkey request
    SIO.on("pubkey_response", ({ peer, publicKeyB64 }) => {
      if (publicKeyB64) setPeerPubMap(prev => ({ ...prev, [peer]: publicKeyB64 }));
    });

    // server forwards a peer's public key (when peer sends theirs)
    SIO.on("peer_pubkey", async ({ from, publicKeyB64 }) => {
      // store it
      setPeerPubMap(prev => ({ ...prev, [from]: publicKeyB64 }));
      // derive AES key if we have our localKeyPair
      if (localKeyPairRef.current) {
        try {
          const aesKey = await deriveAESGCM(localKeyPairRef.current, publicKeyB64);
          derivedKeysRef.current[from] = aesKey;
          console.log("Derived AES key for", from);
        } catch (e) {
          console.warn("derive failed", e);
        }
      }
    });

    return () => {
      SIO.off("connect");
      SIO.off("disconnect");
      SIO.off("message");
      SIO.off("pubkey_response");
      SIO.off("peer_pubkey");
    };
  }, []);

  // helper: derive AES key given our local pair and peer pubB64
  async function deriveAESGCM(ownKP, peerPubB64) {
    const aes = await deriveAESGCMKey(ownKP, peerPubB64); // CryptoKey
    return aes;
  }

  // register username + publish our public key to server
  async function handleRegister() {
    if (!username) return alert("Type a username first");
    // ensure we have a local pub
    if (!localPubB64Ref.current) {
      const { keyPair, publicKeyB64 } = await generateECDHKeyPair();
      localKeyPairRef.current = keyPair;
      localPubB64Ref.current = publicKeyB64;
    }
    SIO.emit("register", { username, publicKeyB64: localPubB64Ref.current });
    alert(`Registered as ${username}. To chat, set peer username and request their pubkey.`);
  }

  // request peer's pubkey from server; then send our pubkey to peer so they can derive
  async function establishSharedKeyWithPeer() {
    if (!peer) return alert("Set peer username");
    // ask server for peer pubkey (if stored)
    SIO.emit("get_pubkey", { requester: username, peer });
    // after short wait, we will get pubkey via pubkey_response event or peer_pubkey event
    // once we have peer pubkey, derive AES and then send our pubkey to peer
    setTimeout(async () => {
      const peerPub = peerPubMap[peer];
      if (peerPub) {
        // derive local key
        const aes = await deriveAESGCM(localKeyPairRef.current, peerPub);
        derivedKeysRef.current[peer] = aes;
        // send our public key to peer so they can derive as well
        SIO.emit("send_pubkey_to", { from: username, to: peer, publicKeyB64: localPubB64Ref.current });
        alert("Shared key established (demo). Now you can send encrypted messages to peer.");
      } else {
        alert("Peer public key not found yet. Make sure peer has registered and saved their pubkey.");
      }
    }, 600);
  }

  // detection WS: connect to CV service to receive detection_broadcasts
  useEffect(() => {
    const ws = new WebSocket("ws://localhost:7001/ws");
    detectWSRef.current = ws;
    ws.onopen = () => console.log("Connected to CV WS");
    ws.onmessage = (evt) => {
      try {
        const data = JSON.parse(evt.data);
        if (data.type === "detection_broadcast") {
          setDetections(data.detections || []);
          // optional: store any keys for objects (server currently broadcasts object_keys for demo only)
        }
      } catch (e) {
        console.warn("Invalid detection data", e);
      }
    };
    ws.onclose = () => console.log("CV WS closed");
    return () => ws.close();
  }, []);

  // camera capture -> send frames to /detect (backend) at low FPS
  useEffect(() => {
    let interval = null;
    let mounted = true;
    const start = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
          await videoRef.current.play();
          const FPS = 1; // gentle default
          interval = setInterval(async () => {
            if (!mounted) return;
            if (!videoRef.current) return;
            const canvas = document.createElement("canvas");
            const sendW = 320, sendH = 240;
            canvas.width = sendW; canvas.height = sendH;
            const ctx = canvas.getContext("2d");
            ctx.drawImage(videoRef.current, 0, 0, sendW, sendH);
            canvas.toBlob(async (blob) => {
              if (!blob) return;
              const fd = new FormData();
              fd.append("file", blob, "frame.jpg");
              try {
                await fetch("http://localhost:7001/detect", { method: "POST", body: fd });
                // detection broadcast will arrive via CV WS
              } catch (e) {
                console.warn("detect failed", e);
              }
            }, "image/jpeg", 0.7);
          }, 1000 / FPS);
        }
      } catch (e) {
        console.error("camera error", e);
      }
    };
    start();
    return () => {
      mounted = false;
      if (interval) clearInterval(interval);
      if (videoRef.current && videoRef.current.srcObject) {
        const tracks = videoRef.current.srcObject.getTracks();
        tracks.forEach(t => t.stop());
      }
    };
  }, []);

  // draw overlay: boxes + simple chat bubbles
  useEffect(() => {
    const canvas = overlayRef.current;
    const video = videoRef.current;
    if (!canvas || !video) return;
    const ctx = canvas.getContext("2d");
    let raf;
    const render = () => {
      const w = canvas.width = video.clientWidth || 640;
      const h = canvas.height = video.clientHeight || 480;
      ctx.clearRect(0, 0, w, h);

      // scale from detection coordinate (320x240) to displayed video
      const srcW = 320, srcH = 240;
      const sx = w / srcW, sy = h / srcH;

      for (const d of detections) {
        const [x1, y1, x2, y2] = d.bbox;
        const left = x1 * sx, top = y1 * sy, width = (x2 - x1) * sx, height = (y2 - y1) * sy;
        ctx.lineWidth = selected === d.object_id ? 3 : 2;
        ctx.strokeStyle = selected === d.object_id ? "#34d399" : "#f59e0b";
        ctx.strokeRect(left, top, width, height);
        ctx.fillStyle = ctx.strokeStyle;
        ctx.font = "14px Inter, Arial";
        ctx.fillText(`${d.class} ${(d.conf*100).toFixed(0)}%`, left + 6, top + 18);

        // draw last two messages (if any)
        const msgs = chats[d.object_id] || [];
        const last = msgs.slice(-2);
        last.forEach((m, idx) => {
          const text = m.plaintext || m.plain || "[encrypted]";
          const bx = left, by = top + height + 16 + idx*20;
          const wText = Math.min(300, ctx.measureText(text).width + 12);
          ctx.fillStyle = "rgba(2,6,23,0.9)";
          ctx.fillRect(bx, by - 14, wText, 18);
          ctx.fillStyle = "#e6eef8";
          ctx.fillText(text, bx + 6, by);
        });
      }

      raf = requestAnimationFrame(render);
    };
    raf = requestAnimationFrame(render);
    return () => cancelAnimationFrame(raf);
  }, [detections, chats, selected]);

  // click on video to select object by mapping click -> detection coords
  function handleVideoClick(e) {
    const rect = e.target.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    const srcW = 320, srcH = 240;
    const scaleX = srcW / rect.width, scaleY = srcH / rect.height;
    const rx = x * scaleX, ry = y * scaleY;
    let picked = null;
    for (const d of detections) {
      const [x1,y1,x2,y2] = d.bbox;
      if (rx >= x1 && rx <= x2 && ry >= y1 && ry <= y2) {
        picked = d.object_id;
        break;
      }
    }
    setSelected(picked);
  }

  // send encrypted message to peer using established derived key
  async function sendMessage() {
    if (!username || !peer) return alert("set username and peer");
    if (!selected) return alert("select an object first");
    if (!input) return;
    const aesKey = derivedKeysRef.current[peer];
    if (!aesKey) return alert("no shared key with peer yet — establish key first");
    try {
      const enc = await encryptAesGcm(aesKey, input);
      const payload = {
        sender: username,
        receiver: peer,
        object_id: selected,
        text_enc: enc,
        ts: Date.now()
      };
      SIO.emit("message", payload);
      // locally show optimistic plaintext
      setChats(prev => {
        const arr = prev[selected] ? [...prev[selected]] : [];
        arr.push({ sender: username, plaintext: input, ts: Date.now() });
        return { ...prev, [selected]: arr };
      });
      setInput("");
    } catch (e) {
      console.error("send failed", e);
      alert("Failed to encrypt/send message");
    }
  }

  // UI render
  return (
    <div style={{ fontFamily: "Inter, Arial, sans-serif", background: "#071021", minHeight: "100vh", color: "#e6eef8", padding: 20 }}>
      <div style={{ maxWidth: 1200, margin: "0 auto 24px", display: "flex", justifyContent: "space-between", alignItems: "center" }}>
        <h2 style={{ margin: 0 }}>ObjectChat • Encrypted Real-Time AR Chat</h2>
        <div style={{ color: "#8fb0d6" }}>{ connected ? "Socket connected" : "Socket disconnected" }</div>
      </div>

      <div style={{ display: "grid", gridTemplateColumns: "320px 1fr 360px", gap: 16 }}>
        {/* left controls */}
        <div style={{ background: "#071425", padding: 12, borderRadius: 10 }}>
          <div style={{ fontWeight: 700, marginBottom: 8 }}>Session</div>
          <div style={{ fontSize: 13, color: "#9fb0d6" }}>Username</div>
          <input style={{ width: "100%", padding: 8, marginTop:6, borderRadius:8, border:"1px solid #213347", background:"#04111a", color:"#e6eef8" }}
                 value={username} onChange={e=>setUsername(e.target.value)} placeholder="your name" />
          <div style={{ height: 10 }} />
          <div style={{ fontSize: 13, color: "#9fb0d6" }}>Peer</div>
          <input style={{ width: "100%", padding: 8, marginTop:6, borderRadius:8, border:"1px solid #213347", background:"#04111a", color:"#e6eef8" }}
                 value={peer} onChange={e=>setPeer(e.target.value)} placeholder="peer username" />
          <div style={{ display:"flex", gap:8, marginTop:10 }}>
            <button onClick={handleRegister} style={{ padding:"8px 12px", borderRadius:8, background:"#06b6d4", border:"none", cursor:"pointer" }}>Register</button>
            <button onClick={establishSharedKeyWithPeer} style={{ padding:"8px 12px", borderRadius:8, background:"#1f6f9b", border:"none", cursor:"pointer" }}>Establish Key</button>
          </div>

          <div style={{ height: 12 }} />
          <div style={{ fontSize: 13, color:"#9fb0d6", marginBottom:6 }}>Selected object</div>
          <div style={{ padding:8, background:"#031422", borderRadius:8 }}>
            <div style={{ fontWeight:700 }}>{ selected || "None" }</div>
            <div style={{ fontSize:12, color:"#7f9bb3", marginTop:6 }}>{ detections.length } objects in view</div>
          </div>

          <div style={{ height:12 }} />
          <div style={{ fontSize:13, color:"#9fb0d6" }}>Encrypted Message</div>
          <textarea value={input} onChange={e=>setInput(e.target.value)} placeholder="Type message..." rows={5}
                    style={{ width:"100%", marginTop:8, borderRadius:8, background:"#04111a", color:"#e6eef8", border:"1px solid #213347", padding:8 }} />
          <div style={{ display:"flex", gap:8, marginTop:8 }}>
            <button onClick={sendMessage} style={{ padding:"8px 12px", borderRadius:8, background:"#16a34a", border:"none", cursor:"pointer" }}>Send</button>
            <button onClick={()=>setInput("")} style={{ padding:"8px 12px", borderRadius:8, background:"#1f6f9b", border:"none" }}>Clear</button>
          </div>

          <div style={{ height:12 }} />
          <div style={{ fontSize:13, color:"#9fb0d6" }}>Known peer public keys</div>
          <div style={{ marginTop:8, maxHeight:160, overflowY:"auto", background:"#041426", padding:8, borderRadius:8 }}>
            { Object.keys(peerPubMap).length === 0 && <div style={{ color:"#5f7f9f" }}>No peer public keys yet</div> }
            { Object.entries(peerPubMap).map(([u, k]) => (
              <div key={u} style={{ marginBottom:8 }}>
                <div style={{ fontWeight:700 }}>{u}</div>
                <div style={{ fontSize:12, color:"#7f9bb3", wordBreak:"break-all" }}>{k.slice(0,60)}{k.length>60?"...":""}</div>
              </div>
            )) }
          </div>
        </div>

        {/* center: video */}
        <div style={{ background:"#071425", padding:12, borderRadius:10 }}>
          <div style={{ fontWeight:700, marginBottom:8 }}>Live Camera</div>
          <div style={{ width:"100%", position:"relative", background:"#000", borderRadius:8, overflow:"hidden" }}>
            <video ref={videoRef} onClick={handleVideoClick} style={{ width:"100%", height:480, objectFit:"cover" }} autoPlay muted />
            <canvas ref={overlayRef} style={{ position:"absolute", left:0, top:0, pointerEvents:"none" }} />
          </div>
          <div style={{ marginTop:10, display:"flex", gap:8 }}>
            <div style={{ fontSize:13, color:"#9fb0d6" }}>Tip: click a bounding box to select object</div>
          </div>
        </div>

        {/* right: object chat history */}
        <div style={{ background:"#071425", padding:12, borderRadius:10 }}>
          <div style={{ fontWeight:700, marginBottom:8 }}>Object Chat</div>
          <div style={{ fontSize:13, color:"#9fb0d6", marginBottom:8 }}>Messages for selected object</div>
          <div style={{ maxHeight:480, overflowY:"auto", paddingRight:8 }}>
            { selected && chats[selected] ? chats[selected].map((m, i) => (
              <div key={i} style={{ display:"flex", flexDirection: m.sender === username ? "row-reverse" : "row", marginBottom:10 }}>
                <div style={{ maxWidth:"80%", background: m.sender === username ? "#0ea5a4" : "#0b1226", color: m.sender === username ? "#02120f" : "#e6eef8", padding:10, borderRadius:10 }}>
                  <div style={{ fontSize:14, fontWeight:600 }}>{m.plaintext || m.plain || "[encrypted]"}</div>
                  <div style={{ fontSize:11, color:"#8ba0c0", marginTop:6 }}>{m.sender} • {new Date(m.ts||Date.now()).toLocaleTimeString()}</div>
                </div>
              </div>
            )) : <div style={{ color:"#5f7f9f" }}>Select an object to see messages</div> }
          </div>
        </div>
      </div>
    </div>
  );
}
